CREATE OR REPLACE FORCE VIEW "SRC_ROLE" ("ID", "SOURCE_CODE", "SOURCE_ID", "LIBELLE", "CODE", "ROLE_ID", "THESE_DEP", "STRUCTURE_ID", "TYPE_STRUCTURE_DEPENDANT_ID") AS
SELECT
	NULL                       AS id,
	tmp.SOURCE_CODE            AS SOURCE_CODE,
	src.ID                     AS source_id,
	--e.id                       AS etablissement_id,
	tmp.LIB_ROJ                AS libelle,
	to_char(tmp.id)            AS code,
	tmp.LIB_ROJ||' '||s.CODE   AS role_id,
	1                          AS these_dep,
	s.ID                       AS STRUCTURE_ID,
	NULL                       AS TYPE_STRUCTURE_DEPENDANT_ID
FROM TMP_ROLE tmp
			 JOIN STRUCTURE s ON s.SOURCE_CODE = tmp.ETABLISSEMENT_ID
			 JOIN ETABLISSEMENT e ON e.STRUCTURE_ID = s.ID
			 JOIN STRUCTURE s ON s.id = e.STRUCTURE_ID
			 JOIN SOURCE src ON src.CODE = tmp.SOURCE_ID;
CREATE OR REPLACE FORCE VIEW "SRC_STRUCTURE" ("ID", "SOURCE_CODE", "CODE", "SOURCE_ID", "TYPE_STRUCTURE_ID", "SIGLE", "LIBELLE", "CODE_PAYS", "LIBELLE_PAYS") AS
SELECT
	NULL              AS id,
	tmp.SOURCE_CODE   as SOURCE_CODE,
	tmp.id            as CODE,
	src.id            AS SOURCE_ID,
	ts.id             as TYPE_STRUCTURE_ID,
	tmp.SIGLE,
	tmp.LIBELLE,
	tmp.CODE_PAYS,
	tmp.LIBELLE_PAYS
FROM TMP_STRUCTURE tmp
