CREATE OR REPLACE FORCE VIEW "V_WF_ETAPE_PERTIN" ("THESE_ID", "ETAPE_ID", "CODE", "ORDRE", "ID") AS
select
	to_number(these_id) these_id,
	to_number(etape_id) etape_id,
	code,
	ordre,
	rownum id
from (
			 --
			 -- validation_page_de_couverture
			 --
			 select
				 t.id as these_id,
				 e.id as etape_id,
				 e.code,
				 e.ordre
			 from these t
							join wf_etape e on e.code = 'VALIDATION_PAGE_DE_COUVERTURE'
			 where t.ETAT_THESE in ('E', 'S') -- thses en cours ou soutenues

			 union all

			 --
			 -- depot_version_originale
			 --
			 select
				 t.id as these_id,
				 e.id as etape_id,
				 e.code,
				 e.ordre
			 from these t
							join wf_etape e on e.code = 'DEPOT_VERSION_ORIGINALE'
			 where t.ETAT_THESE in ('E', 'S') -- thses en cours ou soutenues

			 union all

			 --
			 -- attestations
			 --
			 select
				 t.id as these_id,
				 e.id as etape_id,
				 e.code,
				 e.ordre
			 from these t
							join wf_etape e on e.code = 'ATTESTATIONS'
			 where t.ETAT_THESE in ('E', 'S') -- thses en cours ou soutenues

			 union all

			 --
			 -- autorisation_diffusion_these
			 --
			 select
				 t.id as these_id,
				 e.id as etape_id,
				 e.code,
				 e.ordre
			 from these t
							join wf_etape e on e.code = 'AUTORISATION_DIFFUSION_THESE'
			 where t.ETAT_THESE in ('E', 'S') -- thses en cours ou soutenues

			 union all

			 --
			 -- signalement_these
			 --
			 select
				 t.id as these_id,
				 e.id as etape_id,
				 e.code,
				 e.ordre
			 from these t
							join wf_etape e on e.code = 'SIGNALEMENT_THESE'
			 where t.ETAT_THESE in ('E', 'S') -- thses en cours ou soutenues

			 union all

			 --
			 -- archivabilite_version_originale
			 --
			 select
				 t.id as these_id,
				 e.id as etape_id,
				 e.code,
				 e.ordre
			 from these t
							join wf_etape e on e.code = 'ARCHIVABILITE_VERSION_ORIGINALE'
			 where t.ETAT_THESE in ('E', 'S') -- thses en cours ou soutenues

			 union all

			 --
			 -- depot_version_archivage
			 --
			 select
				 t.id as these_id,
				 e.id as etape_id,
				 e.code,
				 e.ordre
			 from these t
