CREATE OR REPLACE FORCE VIEW "V_WORKFLOW" ("ID", "THESE_ID", "ETAPE_ID", "CODE", "ORDRE", "FRANCHIE", "RESULTAT", "OBJECTIF") AS
SELECT
	ROWNUM id,
	t."THESE_ID",t."ETAPE_ID",t."CODE",t."ORDRE",t."FRANCHIE",t."RESULTAT",t."OBJECTIF"
FROM (
			 --
			 -- VALIDATION_PAGE_DE_COUVERTURE : franchie si version page de couverture valide
			 --
			 SELECT
				 t.id AS    these_id,
				 e.id AS    etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.valide IS NULL THEN 0 ELSE 1 END franchie,
				 CASE WHEN v.valide IS NULL THEN 0 ELSE 1 END resultat,
				 1 objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'VALIDATION_PAGE_DE_COUVERTURE'
							LEFT JOIN V_SITU_VALIDATION_PAGE_COUV v ON v.these_id = t.id

			 UNION ALL

			 --
			 -- DEPOT_VERSION_ORIGINALE : franchie si version originale dpose
			 --
			 SELECT
				 t.id AS    these_id,
				 e.id AS    etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.fichier_id IS NULL
								THEN 0
							ELSE 1 END franchie,
				 CASE WHEN v.fichier_id IS NULL
								THEN 0
							ELSE 1 END resultat,
				 1          objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'DEPOT_VERSION_ORIGINALE'
							LEFT JOIN V_SITU_DEPOT_VO v ON v.these_id = t.id

			 UNION ALL

			 --
			 -- ATTESTATIONS : franchie si donnes saisies
			 --
			 SELECT
				 t.id AS    these_id,
				 e.id AS    etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.attestation_id IS NULL
								THEN 0
							ELSE 1 END franchie,
				 CASE WHEN v.attestation_id IS NULL
								THEN 0
							ELSE 1 END resultat,
				 1          objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'ATTESTATIONS'
							LEFT JOIN V_SITU_ATTESTATIONS v ON v.these_id = t.id

			 UNION ALL

			 --
			 -- AUTORISATION_DIFFUSION_THESE : franchie si donnes saisies
			 --
			 SELECT
				 t.id AS    these_id,
				 e.id AS    etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.diffusion_id IS NULL
								THEN 0
							ELSE 1 END franchie,
				 CASE WHEN v.diffusion_id IS NULL
								THEN 0
							ELSE 1 END resultat,
				 1          objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'AUTORISATION_DIFFUSION_THESE'
							LEFT JOIN V_SITU_AUTORIS_DIFF_THESE v ON v.these_id = t.id

			 UNION ALL

			 --
			 -- SIGNALEMENT_THESE : franchie si donnes saisies
			 --
			 SELECT
				 t.id AS    these_id,
				 e.id AS    etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.description_id IS NULL
								THEN 0
							ELSE 1 END franchie,
				 CASE WHEN v.description_id IS NULL
								THEN 0
							ELSE 1 END resultat,
				 1          objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'SIGNALEMENT_THESE'
							LEFT JOIN V_SITU_SIGNALEMENT_THESE v ON v.these_id = t.id

			 UNION ALL

			 --
			 -- ARCHIVABILITE_VERSION_ORIGINALE : franchie si l'archivabilit de la version originale a t teste
			 --
			 SELECT
				 t.id AS    these_id,
				 e.id AS    etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.THESE_ID IS NULL THEN 0 ELSE 1 END franchie,
				 --            CASE WHEN v.THESE_ID IS NULL THEN
				 --              0 -- test d'archivabilit inexistant
				 --            ELSE
				 --              CASE WHEN v.EST_VALIDE IS NULL THEN
				 --                1 -- test d'archivabilit existant mais rsultat indtermin (plantage)
				 --              ELSE
				 --                CASE WHEN v.EST_VALIDE = 1 THEN
				 --                  1 -- test d'archivabilit russi
				 --                ELSE
				 --                  0 -- test d'archivabilit chou
				 --                END
				 --              END
				 --            END franchie,
				 CASE WHEN v.EST_VALIDE IS NULL OR v.EST_VALIDE = 0 THEN 0 ELSE 1 END resultat,
				 1 objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'ARCHIVABILITE_VERSION_ORIGINALE'
							LEFT JOIN V_SITU_ARCHIVAB_VO v ON v.these_id = t.id

			 UNION ALL

			 --
			 -- DEPOT_VERSION_ARCHIVAGE : franchie si version d'archivage dpose
			 --
			 SELECT
				 t.id AS    these_id,
				 e.id AS    etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.fichier_id IS NULL
								THEN 0
							ELSE 1 END franchie,
				 CASE WHEN v.fichier_id IS NULL
								THEN 0
							ELSE 1 END resultat,
				 1          objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'DEPOT_VERSION_ARCHIVAGE'
							LEFT JOIN V_SITU_DEPOT_VA v ON v.these_id = t.id
							LEFT JOIN fichier f ON f.id = v.fichier_id

			 UNION ALL

			 --
			 -- ARCHIVABILITE_VERSION_ARCHIVAGE : franchie si l'archivabilit de la version d'archivage a t teste
			 --
			 SELECT
				 t.id AS    these_id,
				 e.id AS    etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.EST_VALIDE IS NULL
								THEN 0
							ELSE 1 END franchie,
				 CASE WHEN v.EST_VALIDE IS NULL OR v.EST_VALIDE = 0
								THEN 0
							ELSE 1 END resultat,
				 1          objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'ARCHIVABILITE_VERSION_ARCHIVAGE'
							LEFT JOIN V_SITU_ARCHIVAB_VA v ON v.these_id = t.id

			 UNION ALL

			 --
			 -- VERIFICATION_VERSION_ARCHIVAGE : franchie si vrification de la version originale effectue (peu importe la rponse)
			 --
			 SELECT
				 t.id AS    these_id,
				 e.id AS    etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.EST_CONFORME IS NULL
								THEN 0
							ELSE 1 END franchie,
				 CASE WHEN v.EST_CONFORME IS NULL OR v.EST_CONFORME = 0
								THEN 0
							ELSE 1 END resultat,
				 1          objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'VERIFICATION_VERSION_ARCHIVAGE'
							LEFT JOIN V_SITU_VERIF_VA v ON v.these_id = t.id

			 UNION ALL

			 --
			 -- RDV_BU_SAISIE_DOCTORANT : franchie si donnes doctorant saisies
			 --
			 SELECT
				 t.id AS                      these_id,
				 e.id AS                      etape_id,
				 e.code,
				 e.ORDRE,
				 coalesce(v.ok, 0)            franchie,
				 (CASE WHEN rdv.COORD_DOCTORANT IS NULL
								 THEN 0
							 ELSE 1 END +
					CASE WHEN rdv.DISPO_DOCTORANT IS NULL
								 THEN 0
							 ELSE 1 END)                 resultat,
				 2                            objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'RDV_BU_SAISIE_DOCTORANT'
							LEFT JOIN V_SITU_RDV_BU_SAISIE_DOCT v ON v.these_id = t.id
							LEFT JOIN RDV_BU rdv ON rdv.THESE_ID = t.id

			 UNION ALL

			 --          --
			 --          -- RDV_BU_SAISIE_BU : franchie si donnes BU saisies
			 --          --
			 --          SELECT
			 --            t.id AS                                                                          these_id,
			 --            e.id AS                                                                          etape_id,
			 --            e.code,
			 --            e.ORDRE,
			 --            coalesce(v.ok, 0)                                                                franchie,
			 --            CASE WHEN rdv.MOTS_CLES_RAMEAU IS NULL THEN 0 ELSE 1 END +
			 --            coalesce(rdv.VERSION_ARCHIVABLE_FOURNIE, 0) +
			 --            coalesce(rdv.EXEMPL_PAPIER_FOURNI, 0) +
			 --            coalesce(rdv.CONVENTION_MEL_SIGNEE, 0)                                           resultat,
			 --            4                                                                                objectif
			 --          FROM these t
			 --            JOIN WF_ETAPE e ON e.code = 'RDV_BU_SAISIE_BU'
			 --            LEFT JOIN V_SITU_RDV_BU_SAISIE_BU v ON v.these_id = t.id
			 --            LEFT JOIN RDV_BU rdv ON rdv.THESE_ID = t.id
			 --
			 --       UNION ALL

			 --
			 -- RDV_BU_VALIDATION_BU : franchie si donnes BU saisies ET une validation BU existe
			 --
			 SELECT
				 t.id AS               these_id,
				 e.id AS               etape_id,
				 e.code,
				 e.ORDRE,
				 coalesce(vs.ok, 0) * coalesce(v.valide, 0) franchie,
				 coalesce(vs.ok, 0) + coalesce(v.valide, 0) resultat,
				 2 objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'RDV_BU_VALIDATION_BU'
							LEFT JOIN V_SITU_RDV_BU_SAISIE_BU vs ON vs.these_id = t.id
							LEFT JOIN V_SITU_RDV_BU_VALIDATION_BU v ON v.these_id = t.id

			 UNION ALL




			 --
			 -- DEPOT_VERSION_ORIGINALE_CORRIGEE : franchie si version originale corrige dpose
			 --
			 SELECT
				 t.id AS    these_id,
				 e.id AS    etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.fichier_id IS NULL
								THEN 0
							ELSE 1 END franchie,
				 CASE WHEN v.fichier_id IS NULL
								THEN 0
							ELSE 1 END resultat,
				 1          objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'DEPOT_VERSION_ORIGINALE_CORRIGEE'
							LEFT JOIN V_SITU_DEPOT_VOC v ON v.these_id = t.id

			 UNION ALL

			 --
			 -- ATTESTATIONS_VERSION_CORRIGEE : franchie si donnes saisies
			 --
			 SELECT
				 t.id AS these_id,
				 e.id AS etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.attestation_id IS NULL THEN 0 ELSE 1 END franchie,
				 CASE WHEN v.attestation_id IS NULL THEN 0 ELSE 1 END resultat,
				 1 objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'ATTESTATIONS_VERSION_CORRIGEE'
							LEFT JOIN V_SITU_ATTESTATIONS_VOC v ON v.these_id = t.id

			 UNION ALL

			 --
			 -- AUTORISATION_DIFFUSION_THESE_VERSION_CORRIGEE : franchie si donnes saisies
			 --
			 SELECT
				 t.id AS these_id,
				 e.id AS etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.diffusion_id IS NULL THEN 0 ELSE 1 END franchie,
				 CASE WHEN v.diffusion_id IS NULL THEN 0 ELSE 1 END resultat,
				 1 objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'AUTORISATION_DIFFUSION_THESE_VERSION_CORRIGEE'
							LEFT JOIN V_SITU_AUTORIS_DIFF_THESE_VOC v ON v.these_id = t.id

			 UNION ALL

			 --
			 -- ARCHIVABILITE_VERSION_ORIGINALE_CORRIGEE : franchie si l'archivabilit de la version originale corrige a t teste
			 --
			 SELECT
				 t.id AS    these_id,
				 e.id AS    etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.THESE_ID IS NULL THEN 0 ELSE 1 END franchie,
				 CASE WHEN v.EST_VALIDE IS NULL OR v.EST_VALIDE = 0 THEN 0 ELSE 1 END resultat,
				 1 objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'ARCHIVABILITE_VERSION_ORIGINALE_CORRIGEE'
							LEFT JOIN V_SITU_ARCHIVAB_VOC v ON v.these_id = t.id

			 UNION ALL

			 --
			 -- DEPOT_VERSION_ARCHIVAGE_CORRIGEE : franchie si version d'archivage corrige dpose
			 --
			 SELECT
				 t.id AS    these_id,
				 e.id AS    etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.fichier_id IS NULL
								THEN 0
							ELSE 1 END franchie,
				 CASE WHEN v.fichier_id IS NULL
								THEN 0
							ELSE 1 END resultat,
				 1          objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'DEPOT_VERSION_ARCHIVAGE_CORRIGEE'
							LEFT JOIN V_SITU_DEPOT_VAC v ON v.these_id = t.id
							LEFT JOIN fichier f ON f.id = v.fichier_id

			 UNION ALL

			 --
			 -- ARCHIVABILITE_VERSION_ARCHIVAGE_CORRIGEE : franchie si la version d'archivage corrige est archivable
			 --
			 SELECT
				 t.id AS    these_id,
				 e.id AS    etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.EST_VALIDE = 1 THEN 1 ELSE 0 END franchie,
				 CASE WHEN v.EST_VALIDE = 1 THEN 1 ELSE 0 END resultat,
				 1 objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'ARCHIVABILITE_VERSION_ARCHIVAGE_CORRIGEE'
							LEFT JOIN V_SITU_ARCHIVAB_VAC v ON v.these_id = t.id

			 UNION ALL

			 --
			 -- VERIFICATION_VERSION_ARCHIVAGE_CORRIGEE : franchie si la version corrige est certifie conforme
			 --
			 SELECT
				 t.id AS    these_id,
				 e.id AS    etape_id,
				 e.code,
				 e.ORDRE,
				 CASE WHEN v.EST_CONFORME = 1 THEN 1 ELSE 0 END franchie,
				 CASE WHEN v.EST_CONFORME = 1 THEN 1 ELSE 0 END resultat,
				 1 objectif
			 FROM these t
							JOIN WF_ETAPE e ON e.code = 'VERIFICATION_VERSION_ARCHIVAGE_CORRIGEE'
							LEFT JOIN V_SITU_VERIF_VAC v ON v.these_id = t.id

			 UNION ALL

			 --
			 -- DEPOT_VERSION_CORRIGEE_VALIDATION_DOCTORANT : franchie si la validation attendue existe
			 --
			 SELECT
				 t.id AS               these_id,
				 e.id AS               etape_id,
				 e.code,
				 e.ORDRE,
				 coalesce(v.valide, 0) franchie,
				 coalesce(v.valide, 0) resultat,
