CREATE OR REPLACE VIEW v_diff_structure (source_code, source_id, operation, u_code, u_type_structure_id, u_sigle, u_libelle, s_code, s_type_structure_id, s_sigle, s_libelle, d_code, d_type_structure_id, d_sigle, d_libelle) AS with diff as (
