            CASE WHEN d.CODE <> s.CODE OR (d.CODE IS NULL AND s.CODE IS NOT NULL) OR (d.CODE IS NOT NULL AND s.CODE IS NULL) THEN 1 ELSE 0 END as U_CODE,
    CASE WHEN d.TYPE_STRUCTURE_ID <> s.TYPE_STRUCTURE_ID OR (d.TYPE_STRUCTURE_ID IS NULL AND s.TYPE_STRUCTURE_ID IS NOT NULL) OR (d.TYPE_STRUCTURE_ID IS NOT NULL AND s.TYPE_STRUCTURE_ID IS NULL) THEN 1 ELSE 0 END as U_TYPE_STRUCTURE_ID,
