    d.SIGLE AS D_SIGLE,
    d.LIBELLE AS D_LIBELLE

         FROM STRUCTURE d
        JOIN source src ON src.id = d.source_id AND src.importable = 1
        FULL OUTER JOIN SRC_STRUCTURE s ON s.source_id = d.source_id AND s.SOURCE_CODE = d.SOURCE_CODE
