    select "SOURCE_CODE","SOURCE_ID","OPERATION","U_CODE","U_TYPE_STRUCTURE_ID","U_SIGLE","U_LIBELLE","S_CODE","S_TYPE_STRUCTURE_ID","S_SIGLE","S_LIBELLE","D_CODE","D_TYPE_STRUCTURE_ID","D_SIGLE","D_LIBELLE" from diff
